$(function(){$("#contactForm input,#contactForm textarea").jqBootstrapValidation({preventSubmit:true,submitError:function(a,b,c){},submitSuccess:function(b,g){g.preventDefault();var d=$("input#name").val();var a=$("input#phone").val();var c=$("input#email").val();var f=$("textarea#message").val();var e=d;if(e.indexOf(" ")>=0){e=d.split(" ").slice(0,-1).join(" ")}$.ajax({url:"./bin/contact_me.php",type:"POST",data:{name:d,phone:a,email:c,message:f},cache:false,success:function(){$("#success").html("<div class='alert alert-success'>");$("#success > .alert-success").html("<button type='button' class='close' data-dismiss='alert' aria-hidden='true'>&times;").append("</button>");$("#success > .alert-success").append("<strong>Your message has been sent. </strong>");$("#success > .alert-success").append("</div>");$("#contactForm").trigger("reset")},error:function(){$("#success").html("<div class='alert alert-danger'>");$("#success > .alert-danger").html("<button type='button' class='close' data-dismiss='alert' aria-hidden='true'>&times;").append("</button>");$("#success > .alert-danger").append("<strong>Sorry "+e+" it seems that my mail server is not responding...</strong> Could you please email me directly to <a href='mailto:me@example.com?Subject=Message_Me from myprogrammingblog.com;>me@example.com</a> ? Sorry for the inconvenience!");$("#success > .alert-danger").append("</div>");$("#contactForm").trigger("reset")},})},filter:function(){return $(this).is(":visible")},});$('a[data-toggle="tab"]').click(function(a){a.preventDefault();$(this).tab("show")})});$("#name").focus(function(){$("#success").html("")});